function hasClass(e,n){return e.className.match(new RegExp("(\\s|^)"+n+"(\\s|$)"))}function addClass(e,n){this.hasClass(e,n)||(e.className+=" "+n)}function removeClass(e,n){if(hasClass(e,n)){var a=new RegExp("(\\s|^)"+n+"(\\s|$)");e.className=e.className.replace(a,"")}}function open_menu(){hasClass(document.body,"nav-open")?removeClass(document.body,"nav-open"):addClass(document.body,"nav-open")}window.onload=function(){document.getElementById("toggle-menu").addEventListener("click",open_menu)},$(function(){$("a[href*=#]:not([href=#])").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var e=$(this.hash);if(e=e.length?e:$("[name="+this.hash.slice(1)+"]"),e.length)return removeClass(document.body,"nav-open"),$("html,body").animate({scrollTop:e.offset().top},1e3),!1}})});